// Helper function to get jutsu by ID
export function getJutsuById(id) {
    return JUTSU_DATABASE.find((jutsu) => jutsu.id === id);
}
// Helper function to get jutsu by level
export function getJutsuByLevel(level) {
    return JUTSU_DATABASE.filter((jutsu) => jutsu.unlockLevel <= level);
}
// Helper function to get jutsu by rarity
export function getJutsuByRarity(rarity) {
    return JUTSU_DATABASE.filter((jutsu) => jutsu.rarity === rarity);
}
